CREATE TABLE `shop`.`products` ( `id` INT(10) NOT NULL AUTO_INCREMENT , `category` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `name` VARCHAR(200) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `sku` VARCHAR(64) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `price` FLOAT(50) NOT NULL , `image` VARCHAR(100) NOT NULL , `description` TEXT CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `create_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `update_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB;